import styled from 'styled-components';
import MakeRoomComponent from '../components/MainPages/MakeRoomComponent';
import MotivationText from '../components/MainPages/MotivationText';
import Category from '../components/MainPages/Category';
import { useState } from 'react';
const Main = () => {
  //더미데이터
  const categoryList = {
    프론트엔드: false,
    백엔드: false,
    앱개발: false,
    데이터사이언스: false,
    데브옵스: false,
    게임: false,
    시스템: false,
  };
  const [selectedCategory, setSelectedCategory] = useState(categoryList);

  const handleCategoryClick = (e) => {
    const value = e.currentTarget.innerHTML;
    let newList = selectedCategory;
    if (!selectedCategory[value]) {
      newList[value] = true;
    } else {
      newList[value] = false;
    }
    setSelectedCategory(newList);
  };

  return (
    <div>
      <UpperBox>
        <MakeRoomComponent />
        <MotivationText />
      </UpperBox>
      <LowerBox>
        <MainBox>
          <Category
            categoryList={Object.keys(selectedCategory)}
            onClick={handleCategoryClick}
          />
          {/* RoomBoxComponent */}
        </MainBox>
        <InfoBox>
          {/* InfoComponent */}
          {/* TreeBoxComponent */}
        </InfoBox>
      </LowerBox>
    </div>
  );
};
export default Main;

const UpperBox = styled.div`
  display: flex;
  justify-content: space-around;
`;

const LowerBox = styled.div``;
const MainBox = styled.div``;
const InfoBox = styled.div``;
