import styled from 'styled-components';
import MakeRoomComponent from '../components/MainPages/MakeRoomComponent';
import MotivationText from '../components/MainPages/MotivationText';
import Category from '../components/MainPages/Category';
import Room from '../components/MainPages/Room';
import Info from '../components/MainPages/Info';
import Tree from '../components/MainPages/Tree';
import { useState } from 'react';
const Main = () => {
  //더미데이터
  const tempCategoryList = {
    프론트엔드: false,
    백엔드: false,
    앱개발: false,
    데이터사이언스: false,
    데브옵스: false,
    게임: false,
    시스템: false,
  };
  //방 정보 더미데이터
  const roomDatas = [
    {
      room_id: 1,
      author: 'jongbin',
      title: '투썸에서 공부해요',
      expected_users: 6,
      expected_schedules: 60,
      tags: '프론트엔드',
      room_type: 1,
      room_status: 'open',
      thumbnail: '.../images/tempImage.jpg',
    },
    {
      room_id: 2,
      author: 'jongbin',
      title: '투썸에서 공부해요',
      expected_users: 6,
      expected_schedules: 60,
      tags: '프론트엔드',
      room_type: 1,
      room_status: 'open',
      thumbnail: '../images/tempImage.jpg',
    },
  ];

  const [categoryList, setCategoryList] = useState(tempCategoryList);
  const handleCategoryClick = (e) => {
    const value = e.currentTarget.innerHTML;
    if (categoryList[value]) {
      setCategoryList((current) => {
        const newList = { ...current };
        newList[value] = false;
        return newList;
      });
    } else {
      setCategoryList((current) => {
        const newList = { ...current };
        newList[value] = true;
        return newList;
      });
    }
  };

  return (
    <MainBox>
      <UpperBox>
        <StyledImage />
        <div>큐피소개</div>
        <div>문의하기</div>
      </UpperBox>
      <MediumBox>
        <MotivationText />
        <Category categoryList={categoryList} onClick={handleCategoryClick} />
      </MediumBox>
      <LowerBox>
        <Room roomDatas={roomDatas} />
        <RightBox>
          <Info />
          <Tree />
          <MakeRoomComponent />
        </RightBox>
      </LowerBox>
    </MainBox>
  );
};
export default Main;

const MainBox = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-basis: 100%;
  flex-direction: column;
`;

const UpperBox = styled.div`
  display: flex;
  flex-basis: 10%;
  align-items: center;
`;

const MediumBox = styled.div`
  display: flex;
  flex-basis: 10%;
`;

const LowerBox = styled.div`
  padding: 0 10px 10px 10px;
  display: flex;
  flex-basis: 80%;
  justify-content: space-around;
`;
const RightBox = styled.div`
  display: flex;
  flex-direction: column;
  flex-basis: 30%;
  flex-wrap: nowrap;
  flex-direction: column;
`;

const StyledImage = styled.div`
  width: 100px;
  height: 100px;
  background-image: url('../images/logo.png');
`;
