import styled from 'styled-components';

//DummyData
import InfoDummyData from '../../DummyData/InfoDummyData';
const Info = () => {
  return (
    <InfoBox>
      <InfoImage userImg={InfoDummyData.user_img} />
      <p>{InfoDummyData.user_name}</p>
      <StudyTime>
        <p>공부시간</p>
        <p>{InfoDummyData.study_time}</p>
      </StudyTime>
      <InfoTags>
        {InfoDummyData.user_tags.map((tag) => {
          return <Tag key={InfoDummyData.user_id}>{'#' + tag}</Tag>;
        })}
      </InfoTags>
    </InfoBox>
  );
};

export default Info;
const InfoBox = styled.div`
  border-radius: 10px;
  margin: 10px;
  padding: 10px;
  display: flex;
  flex-wrap: wrap;
  flex-basis: 75%;
  justify-contenet: center;
  background-color: rgba(217, 217, 217, 0.6); //#d9d9d9
`;

const InfoImage = styled.div`
  border-radius: 10px;
  margin: 10px;
  background-image: url(${(props) => process.env.PUBLIC_URL + props.user_img});
  background-size: cover;
`;

const InfoTags = styled.div``;

const StudyTime = styled.div``;

const Tag = styled.div`
  display: flex;
  flex-grow: 1;
  padding: 12px;
  margin: 3px;
  border-radius: 8px;
  align-items: center;
  justify-content: center;
`;
