import styled from 'styled-components';
import MakeRoomComponent from '../components/MainPages/MakeRoomComponent';
import MotivationText from '../components/MainPages/MotivationText';
import Category from '../components/MainPages/Category';
import { useState, useEffect } from 'react';
const Main = () => {
  //더미데이터
  const tempCategoryList = {
    프론트엔드: 'default',
    백엔드: 'default',
    앱개발: 'default',
    데이터사이언스: 'default',
    데브옵스: 'default',
    게임: 'default',
    시스템: 'default',
  };

  const [categoryList, setCategoryList] = useState(tempCategoryList);
  useEffect(() => console.log('바뀜'), [categoryList]);
  const handleCategoryClick = (e) => {
    const value = e.currentTarget.innerHTML;
    const newList = { ...categoryList };
    if (!categoryList[value]) {
      setCategoryList((current) => {
        const newList = { ...current };
        newList[value] = 'clicked';
        return newList;
      });
    } else {
      setCategoryList((current) => {
        const newList = { ...current };
        newList[value] = 'clicked';
        return newList;
      });
    }
  };

  return (
    <div>
      <UpperBox>
        <MakeRoomComponent />
        <MotivationText />
      </UpperBox>
      <LowerBox>
        <MainBox>
          <Category categoryList={categoryList} onClick={handleCategoryClick} />
          {/* RoomBoxComponent */}
        </MainBox>
        <InfoBox>
          {/* InfoComponent */}
          {/* TreeBoxComponent */}
        </InfoBox>
      </LowerBox>
    </div>
  );
};
export default Main;

const UpperBox = styled.div`
  display: flex;
  justify-content: space-around;
`;

