import styled from 'styled-components';
const Category = ({ onClick, categoryList }) => {
  return (
    <CategoryBox list={categoryList}>
      {Object.keys(categoryList).map((name) => (
        <CategoryContent key={name} onClick={onClick} idx={name} list={list}>
          {name}
        </CategoryContent>
      ))}
    </CategoryBox>
  );
};
export default Category;

const CategoryContent = styled.button`
  padding: 15px;
  margin: 3px;
  border-radius: 8px;
  background-color: rgba(10, 10, 35, 0.8);
  color: #ffffff;
  &:hover {
    background-color: rgba(125, 125, 160, 0.8);
    transition: 0.5s;
  }
  ${(props) => {
    if (!props.list[props.idx]) {
      return `background-color: rgba(10, 10, 35, 0.8);
            &:hover {
                background-color: rgba(125, 125, 160, 0.8);
                transition: 0.5s;
              }`;
    } else {
      return `background-color: rgba(125, 125, 160, 0.8);
        &:hover {
            background-color: rgba(10, 10, 35, 0.8)
            transition: 0.5s;
          }`;
    }
  }}
`;
const CategoryBox = styled.div`
  border-radius: 10px;
  margin: 5px;
  padding: 10px;
  display: flex;
  flex-wrap: wrap;
  justify-contenet: center;
  background-color: #d9d9d9;
`;
