import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { Swiper, SwiperSlide } from 'swiper/react';
import '../../styles/Swiper.css';

const ROOM_COUNT = 8;

const Room = () => {
  //방 정보 더미데이터
  const roomDatas = [
    {
      room_id: 1,
      author: 'jongbin',
      title: '투썸에서 공부해요',
      expected_users: 6,
      present_users: 1,
      expected_schedules: 60,
      tags: ['프론트엔드', '백엔드'],
      room_type: 1,
      room_status: 'open',
      thumbnail: '/img/tempImage.jpg',
    },
    {
      room_id: 2,
      author: 'jongbin',
      title: '투썸에서 공부해요',
      expected_users: 6,
      present_users: 1,
      expected_schedules: 60,
      tags: ['프론트엔드', '백엔드'],
      room_type: 1,
      room_status: 'open',
      thumbnail: '/img/tempImage.jpg',
    },
  ];

  function divideRoom(roomDatas) {
    const slideNum = Math.ceil(roomDatas.length / ROOM_COUNT);
    let count = 0;
    let swiper = [];
    let swiperSlide;
    for (const roomData of roomDatas) {
      swiperSlide = [];
      for (let i = 0; i < ROOM_COUNT; i++) {
        if (count >= roomDatas.length) {
          swiper.push(swiperSlide);
          return swiper;
        }
        swiperSlide.push(
          <Board to="/" key={roomData.room_id}>
            <BoardImage $thumbnail={roomData.thumbnail}></BoardImage>
            <RightRoomBox>
              <BoardTitle>{roomData.title}</BoardTitle>
              <LowerRoomBox>
                <BoardTags>
                  {roomData.tags.map((tag) => {
                    return <Tag key={roomData.id}>{'#' + tag}</Tag>;
                  })}
                </BoardTags>
                <UserCount>
                  {String(roomData.present_users) +
                    '/' +
                    String(roomData.expected_users)}
                </UserCount>
              </LowerRoomBox>
            </RightRoomBox>
          </Board>
        );
        count++;
      }
      swiper.push(swiperSlide);
    }
  }

  return <RoomBox>{divideRoom(roomDatas)}</RoomBox>;
  //return <RoomBox>{rooms}</RoomBox>;
};
export default Room;

const RoomBox = styled.div`
  overflow-y: auto;
  border-radius: 10px;
  margin: 10px;
  padding: 10px;
  display: flex;
  flex-basis: 100%;
  flex-wrap: wrap;
  justify-contenet: center;
  background-color: rgba(217, 217, 217, 0.6); //#d9d9d9
`;

const Board = styled(Link)`
  display: flex;
  flex-basis: 45%;
  height: 20%;
  border-radius: 10px;
  margin: 10px;
  padding: 10px;
  justify-contenet: center;
  background-color: #ffffff;
  color: black;
  text-decoration: none;
`;
const BoardImage = styled.div`
  border-radius: 10px;
  flex-basis: 25%;
  margin: 10px;
  background-image: url(${(props) =>
    process.env.PUBLIC_URL + props.$thumbnail});
  background-size: cover;
`;
const BoardTitle = styled.div`
  display: flex;
  flex-basis: 80%;
  border-radius: 10px;
  margin: 10px;
  padding: 10px;
  background-color: rgba(217, 217, 217, 0.6); //#d9d9d9
  align-items: center;
  justify-content: center;
`;
const BoardTags = styled.div`
  display: flex;
  flex-basis: 60%;
`;
const UserCount = styled.div`
  display: flex;
  flex-basis: 40%;
  margin: 5px;
  align-items: center;
  justify-content: center;
  border-radius: 10px;
  background-color: rgba(217, 217, 217, 0.6); //#d9d9d9
`;
const Tag = styled.div`
  display: flex;
  flex-grow: 1;
  padding: 12px;
  margin: 3px;
  border-radius: 8px;
  align-items: center;
  justify-content: center;
`;
const LowerRoomBox = styled.div`
  border-radius: 10px;
  margin: 5px;
  padding: 5px;
  display: flex;
  flex-basis: 20%;
  justify-contenet: center;
`;
const RightRoomBox = styled.div`
  border-radius: 10px;
  display: flex;
  flex-direction: column;
  flex-basis: 75%;
  justify-contenet: center;
  font-size: 18px;
  font-weight: 700;
`;
