import styled from 'styled-components';
import MakeRoomComponent from '../components/MainPages/MakeRoomComponent';
import MotivationText from '../components/MainPages/MotivationText';
import Category from '../components/MainPages/Category';
import { useState } from 'react';
const Main = () => {
  //더미데이터
  const tempCategoryList = {
    프론트엔드: 'default',
    백엔드: 'default',
    앱개발: 'default',
    데이터사이언스: 'default',
    데브옵스: 'default',
    게임: 'default',
    시스템: 'default',
  };
  const [categoryList, setCategoryList] = useState(tempCategoryList);

  const handleCategoryClick = (e) => {
    const value = e.currentTarget.innerHTML;
    let newList = categoryList;
    if (!categoryList[value]) {
      newList[value] = true;
      e.currentTarget.style.backgroundColor = 'rgba(125, 125, 160, 0.8)';
    } else {
      newList[value] = false;
      e.currentTarget.style.backgroundColor = 'rgba(10, 10, 35, 0.8)';
    }
    setCategoryList(newList);
  };

  return (
    <div>
      <UpperBox>
        <MakeRoomComponent />
        <MotivationText />
      </UpperBox>
      <LowerBox>
        <MainBox>
          <Category CategoryList={categoryList} onClick={handleCategoryClick} />
          {/* RoomBoxComponent */}
        </MainBox>
        <InfoBox>
          {/* InfoComponent */}
          {/* TreeBoxComponent */}
        </InfoBox>
      </LowerBox>
    </div>
  );
};
export default Main;

const UpperBox = styled.div`
  display: flex;
  justify-content: space-around;
`;

const LowerBox = styled.div``;
const MainBox = styled.div``;
const InfoBox = styled.div``;
