import styled from 'styled-components';
import MakeRoomComponent from '../components/MainPages/MakeRoomComponent';
import MotivationText from '../components/MainPages/MotivationText';
import Category from '../components/MainPages/Category';
import Room from '../components/MainPages/Room';
import Info from '../components/MainPages/Info';
import Tree from '../components/MainPages/Tree';
import { useState } from 'react';
const Main = () => {
  //더미데이터
  const tempCategoryList = {
    프론트엔드: false,
    백엔드: false,
    앱개발: false,
    데이터사이언스: false,
    데브옵스: false,
    게임: false,
    시스템: false,
  };
  //방 정보 더미데이터
  const roomDatas = [
    {
      room_id: 1,
      author: 'jongbin',
      title: '투썸에서 공부해요',
      expected_users: 6,
      expected_schedules: 60,
      tags: '프론트엔드',
      room_type: 1,
      room_status: 'open',
      thumbnail: 'temp_image',
    },
    {
      room_id: 2,
      author: 'jongbin',
      title: '투썸에서 공부해요',
      expected_users: 6,
      expected_schedules: 60,
      tags: '프론트엔드',
      room_type: 1,
      room_status: open,
      thumbnail: 'temp_image',
    },
  ];

  const [categoryList, setCategoryList] = useState(tempCategoryList);
  const handleCategoryClick = (e) => {
    const value = e.currentTarget.innerHTML;
    if (categoryList[value]) {
      setCategoryList((current) => {
        const newList = { ...current };
        newList[value] = false;
        return newList;
      });
    } else {
      setCategoryList((current) => {
        const newList = { ...current };
        newList[value] = true;
        return newList;
      });
    }
  };

  return (
    <MainBox>
      <UpperBox>
        <MakeRoomComponent />
        <MotivationText />
      </UpperBox>
      <LowerBox>
        <LeftBox>
          <Category categoryList={categoryList} onClick={handleCategoryClick} />
          <Room roomDatas={roomDatas} />
        </LeftBox>
        <RightBox>
          <Info />
          <Tree />
        </RightBox>
      </LowerBox>
    </MainBox>
  );
};
export default Main;

const MainBox = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-basis: 100%;
  flex-direction: column;
`;

const UpperBox = styled.div`
  display: flex;
  flex-basis: 10%;
  justify-content: space-around;
  align-items: center;
`;

const LowerBox = styled.div`
  padding: 0 10px 10px 10px;
  display: flex;
  flex-basis: 90%;
  justify-content: space-around;
`;
const LeftBox = styled.div`
  display: flex;
  flex-direction: column;
  flex-basis: 70%;
  flex-wrap: nowrap;
  @media screen and (max-width: 900px) {
    flex-direction: column;
    flex-basis: 100%;
  }
`;
const RightBox = styled.div`
  display: flex;
  flex-direction: column;
  flex-basis: 30%;
  flex-wrap: nowrap;
  flex-direction: column;
  @media screen and (max-width: 900px) {
    display: none;
  }
`;
