import styled from 'styled-components';
//category 요소 생성 함수
const Category = ({ onClick, categoryList }) => {
  function makeCategoryBox(categoryList) {
    const result = [];
    for (const [key, value] of Object.entries(categoryList)) {
      result.push(
        <CategoryContent key={key} onClick={onClick} value={value}>
          {key}
        </CategoryContent>
      );
    }
    console.log(result);
    return result;
  }

  return <CategoryBox>{makeCategoryBox(categoryList)}</CategoryBox>;
};
export default Category;

const CategoryContent = styled.button`
  padding: 15px;
  margin: 3px;
  border-radius: 8px;
  background-color: rgba(10, 10, 35, 0.8);
  color: #ffffff;
  &:hover {
    background-color: rgba(125, 125, 160, 0.8);
    transition: 0.3s;
  }
  ${(props) => {
    switch (props.$value) {
      case 'default':
        return `
        background-color: rgba(10, 10, 35, 0.8);
        &:hover {
          background-color: rgba(125, 125, 160, 0.8);
          transition: 0.3s;
        }`;
      case 'clicked':
        return `
        background-color: rgba(125, 125, 160, 0.8);
        &:hover {
          background-color: rgba(10, 10, 35, 0.8);
          transition: 0.3s;
        }`;
    }
  }}
`;
const CategoryBox = styled.div`
  border-radius: 10px;
  margin: 5px;
  padding: 10px;
  display: flex;
  flex-wrap: wrap;
  justify-contenet: center;
  background-color: #d9d9d9;
`;
