import styled from 'styled-components';
const Room = ({ roomDatas }) => {
  function makeRoom(roomDatas) {
    const result = [];
    roomDatas.map((roomData) => {
      result.push(
        <Board
          key={roomData.room_id}
          thumbnail={roomData.thumbnail}
          title={roomData.title}
          tags={roomData.tags}
          room_status={roomData.room_status}
          expected_user={roomData.expected_users}
        >
          <BoardImage thumbnail={roomData.thumbnail}></BoardImage>
        </Board>
      );
    });
    return result;
  }
  return <RoomBox>{makeRoom(roomDatas)}</RoomBox>;
};
export default Room;

const RoomBox = styled.div`
  overflow-y: auto;
  border-radius: 10px;
  margin: 10px;
  padding: 10px;
  display: flex;
  flex-basis: 100%;
  flex-wrap: wrap;
  justify-contenet: center;
  background-color: rgba(217, 217, 217, 0.6); //#d9d9d9
  @media screen and (max-width: 1335px) {
    flex-basis: 75%;
  }
`;

const Board = styled.div`
  flex-basis: 45%;
  height: 20%;
  border-radius: 10px;
  margin: 10px;
  padding: 10px;
  justify-contenet: center;
  background-color: #ffffff;
  @media screen and (max-width: 1335px) {
    flex-basis: 100%;
    height: 10%;
  }
`;
const BoardImage = styled.div`
  background-image: url('./images/wallpaper.jpeg');
  background-size: 100%;
`;
